*,*:before,*:after{-webkit-box-sizing:border-box;box-sizing:border-box
}
.about-content{max-width:40rem;margin:0 auto;padding-top:60px
}
.about-content img{margin:0 auto;width:100%;padding:2.25rem
}
.copy,.footer,.features,.copyright{font-size:1rem;line-height:1.375rem;font-weight:700
}
@media (min-width: 46.25em){
.copy,.footer,.features,.copyright{font-size:1.3125rem;line-height:1.8125rem
}
}
.copy p:first-child{text-indent:2.25rem
}
.copyright{text-indent:0;margin-bottom:3.75rem;margin-top:7.5rem
}
.features{color:black;text-align:center;text-indent:0;font-family:'LeviReBrushed';text-transform:uppercase;max-width:30rem;margin:0 auto;margin-top:3.75rem;margin-bottom:3.75rem
}
.features .floating-number{color:white;font-family:"Roboto",-apple-system,BlinkMacSystemFont,"Segoe UI","Oxygen","Ubuntu","Cantarell","Fira Sans","Droid Sans","Helvetica Neue",sans-serif;font-weight:700;font-size:2.25rem;position:absolute;top:-20px;z-index:-1;left:20%
}
.features .feature{position:relative;cursor:pointer
}
.features .feature:nth-child(2) .floating-number{left:75%;top:30px
}
.features .feature:nth-child(3) .floating-number{left:30%;top:100px
}
.features .feature:hover ul,.features .feature:active ul{height:auto
}
.features .feature:hover li,.features .feature:active li{-webkit-transform:scaleY(1);-ms-transform:scaleY(1);transform:scaleY(1);opacity:1
}
.features h3{font-weight:normal;font-size:3rem;line-height:1.2;margin-bottom:1.5rem
}
.features ul{padding:0;margin:0;list-style:none
}
@media (min-width: 61.25em){
.features ul{height:0
}
}
.features ul li{-webkit-transition:all .5s ease;-o-transition:all .5s ease;transition:all .5s ease;font-weight:normal
}
@media (min-width: 61.25em){
.features ul li{opacity:0;-webkit-transform:scaleY(0);-ms-transform:scaleY(0);transform:scaleY(0)
}
}
.footer{text-align:left
}
.footer p{text-indent:0
}
h1{text-indent:2.25rem;padding:0 12px;margin-bottom:1.5rem;font-size:2.25rem
}
@media (min-width: 46.25em){
h1{font-size:4rem
}
}
.about-enter-active,.about-leave-active{-webkit-transition:all .6s ease-in-out;-o-transition:all .6s ease-in-out;transition:all .6s ease-in-out;margin-top:0;opacity:1
}
.about-enter,.about-leave-active{margin-top:100vh;opacity:0
}
